export default function stringifyViewConfig(viewConfig: any): string {
  return JSON.stringify(
    viewConfig,
    (key, val) => {
      if (typeof val === 'function') {
        return `Æ’ ${val.name}`;
      }
      return val;
    },
    2,
  );
}
